import { useState, useEffect } from "react";
import "./Quiz.css";

const Quiz = ({ questions, questionNumber }) => {
  const [displayQuestion, setDisplayQuestion] = useState(null);

  useEffect(() => {
    setDisplayQuestion(questions[questionNumber - 1]);

  }, [questions, questionNumber]);

  useEffect(()=> {
    displayQuestion.answers.map((a)=> {
        console.log(a);
    })
  },[displayQuestion]);

  return (
    // <div className="quiz">
    //     <div className="question">
    //         {displayQuestion?.question}
    //     </div>

    // </div>
    <>
      <div class="flex flex-wrap mt-16 ">
        <div class="w-full sm:w-1/2 md:w-3/4 flex flex-col p-3 mx-auto">
          <div class="bg-emerald-200 rounded-lg shadow-lg overflow-hidden flex-1 flex flex-col">
            <div
              class="bg-cover font-bold text-4xl border-b-2 border-gray-300 p-4 justify-left text-left "
            >
            Question 1 
            <p className="text-xl font-light my-6">
                {displayQuestion?.question}

            </p>
            </div>
            <div
              class="p-4 flex-1 flex flex-col"

            >
              <h3 class="mb-4 text-2xl">asdasdsa</h3>
              <div class="mb-4 text-grey-darker text-sm flex-1">

                    {displayQuestion?.answers.map((a)=>{
                        <p >
                            {a.text}
                        </p>

                    })}
              </div>
              <a
                href="/"
                class="border-t border-grey-light pt-2 text-xs text-grey hover:text-red uppercase no-underline tracking-wide"

              >
                How well do you know me ?
              </a>
            </div>
          </div>
        </div>




      </div>
    </>
  );
};

export default Quiz;
